package gameframe;import javax.swing.*;import java.awt.*;import java.awt.image.BufferedImage;import java.util.Observable;import java.util.Observer;/** * Created by mathi on 20/06/2017. */public class GamePanel extends JPanel implements Observer {    private BufferedImage map;    public GamePanel(/*IGraphicsBuilder graphicsBuilder*/) {        map = new BufferedImage(1, 1, BufferedImage.TYPE_INT_RGB);    }    @Override    public void update(Observable o, Object arg) {    }    @Override    public void paintComponent(Graphics graphics) {        graphics.drawImage(map, 0, 0, null);    }    public void setImageMap(BufferedImage imageMap) {        this.map = imageMap;        this.repaint();    }}